import React, { useState } from "react";
import './ExpenseForm.css';

function ExpenseForm() {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    /* const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount: '',
        enteredDate: '',

    }); */
    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value); /*pega valor digitado a cada momento*/
        /* setUserInput({

            ...userInput,
            enteredTitle: event.target.value,
        }) */
        {/* operador spreed, pega todo os dados e sobrepoem eles*/ }
        /* setUserInput((prevState) => {/*estado anterior = prevState
             return { ...prevState, enteredTitle: event.target.value } 
        })*/
    }
    const amountChangeHandler = event => {
        setEnteredAmount(event.target.value);
        /* setUserInput({

            ...userInput,
            enteredAmount: event.target.value,
        }) */
        /*
        setUserInput((prevState) => {/*estado anterior = prevState
             return { ...prevState, enteredAmount: event.target.value } 
        }) */
    }
    const dateChangeHandler = event => {
        setEnteredDate(event.target.value);
        /*  setUserInput({

            ...userInput,
            enteredDate: event.target.value,
        }) */
        /* setUserInput((prevState) => {
            return { ...prevState, enteredDate: event.target.value }
        }) */
    }

    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate),
        }
        console.log(expenseData);
    }
    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler} />
                </div>
                <div className="new-expense__control" >
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler} />
                    {/*onChange Ã© assionado quando clicamos na input mesmo que onInput*/}
                </div >
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" mina="2019-01-01" max="2022-12-31" onChange={dateChangeHandler} />
                </div>
            </div >
            <div className="new-expense__actions">
                <button type="submit">Add expense</button>
            </div>
        </form >
    )
}

export default ExpenseForm;
